    U+=0.5*R;
    Neighborhood
    Q = B(U+.25*rot(.2*iTime)*A(U).xy)-div;
    Q = sin(Q);
    Mouse Q.x = 1.;
    Init Q = sin(.1*U.xxxx)*cos(U.y);
}

