void mainImage( out vec4 Q, in vec2 U )
{
    vec4 a = A(U);
    Q = vec4(a.z,0.5*(a.w+a.z),a.w,1);
}

vec2 init (vec2 U) {
	return vec2(cos(U.x),sin(U.x))*exp(-.01*dot(U-0.5*R,U-0.5*R));
}
void mainImage( out vec4 Q, in vec2 U )
{
	Q = vec4(0);
    // Fourier transform over y
    vec2 w = 2.*pi*(U-0.5)/R;
    for (float i = 0.; i < R.y; i++) {
        Q.xy += A(vec2(U.x,i)).xy*e(-(w.y-pi)*i);
        Q.zw += B(vec2(U.x,i)).zw*e(w.y*i)/R.y;
    }
	Q.zw *= mod(U.x+U.y,2.)*2.-1.;
}

#define R (iResolution.xy)
#define A(U) texelFetch(iChannel0,ivec2(U),0)
#define B(U) texelFetch(iChannel1,ivec2(U),0)
#define D(U) texelFetch(iChannel3,ivec2(U),0)
#define pi 3.14159265359
mat2 e (float t) {
    float c = cos(t),s = sin(t);
	return mat2(c,-s,s,c);
}

void mainImage( out vec4 Q, in vec2 U )
{
	Q = vec4(0);
    vec2 w = 2.*pi*(U-0.5)/R;
    // Fourier transform over x
    for (float i = 0.; i < R.x; i++) {
        Q.xy += A(vec2(i,U.y)).xy*e(-(w.x-pi)*i);
        Q.zw += B(vec2(i,U.y)).xy*e(w.x*i)/R.x;
    }
    w -= pi;
    // rotate proportional to frequency squared
    // shrodinger equation
    Q.xy *= e(dot(w,w)*sin(iTime*.1)*100.);
}
