void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
	vec3 color=vec3(0.0);

    float aci=atan(-uv.y+0.5,uv.x-0.5)*0.1;
    float uzun=length(uv-vec2(0.5,0.5));
    color.r +=sin(uzun*50.0+aci*40.0+sin(iTime)*3.0);
    color.g +=cos(uzun*50.0+aci*30.0+cos(iTime));
    color.b +=tan(uzun*50.0+aci*30.0+cos(iTime));






    fragColor = vec4(color,1.0);
}
