float pattern(vec2 uv, float a, float b, float c, float d, float e)
{
    float x = uv.x + (cos(a) / b);
    float y = uv.y + (tan(c) / d);    
 	return texture(iChannel0, vec2(x,y)/e).r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    float a = pattern(
        uv,
        uv.y*9.0 + iTime*3.0, 20.0,
        uv.x/2.0 + cos(iTime / 2.0) / 2.0, 1.0,
        3.0);

    float b = pattern(
        uv,
        uv.y*7.0 + iTime*0.5, 100.0,
        uv.x/3.0 + cos(iTime / 1.6) / 2.0, 2.0,
        2.0);

    float c = pattern(
        uv,
        uv.y*8.0 + iTime*4.0, 10.0,
        uv.x/4.0 + cos(iTime / 1.6) / 2.0, 6.0,
        6.0);


    if (a > 0.6 || b > 0.6)
    {
        fragColor.r = a / 1.5 + b*0.2;
     	fragColor.g = b / 1.6 + a*0.2; 
        fragColor.b = 0.2;
        fragColor = vec4(mix(fragColor.rgb, vec3(c,c,c),0.4), 1.0);
    }
    else
    {
     	fragColor = vec4(vec3(c),1.0);
        fragColor.b = 1.0;
    }


    fragColor = vec4(mix(fragColor.rgb, vec3(0.0,0.1,0.4), 0.3), 1.0);

}
