vec3 Dissolve(in vec3 inColor, vec2 uv)
{
    float speed = 4.0f;
    float col = cos(iTime * speed) * 1.f;

    float dissolver = texture(iChannel1, uv).r;

    if (dissolver * col > 0.2f)
        return vec3(0.f, 0.f, 0.f);
    if (dissolver * col > 0.14f)
        return vec3(0.7f, 0.3f, 0.4f);

    return inColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    fragColor = texture(iChannel0, uv);

    // Output to screen
    fragColor = vec4(Dissolve(fragColor.rgb, uv), fragColor.a);
}
