float sfloor( float v, int n){
    float o = v;
    for(int i = 0; i<n; i++){
    	o = o - sin(2.*PI*o)/(2.*PI);
    }
    return o;
}


vec3 sfloor( vec3 v, int n){
    vec3 o = v;
    for(int i = 0; i<n; i++){
    	o = o - sin(2.*PI*o)/(2.*PI);
    }
    return o;
}

