void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy*vec2(.5,1.);

    // Alternating b/w bins
    if (uv.y>.95)          { fragColor=.1*vec4( int(uv.x*255.   ) %2==0); return; }
    if (abs(uv.y-.8)<.025) { fragColor=.1*vec4( int(uv.x*255.+.5) %2==0); return; }
    if (uv.y<.05)          { fragColor=.1*vec4( int(uv.x*256.   ) %2==0); return; }

    // no gamma correction, just plain 0. .. 1. to display
    fragColor = vec4(uv.x);
}
