uniform vec2 u_resolution;
uniform float u_time;

vec2 brickTileHorizontal(vec2 _st, float _zoom){
    _st *= _zoom;

    // Here is where the offset is happening
    _st.x += mod(_st.y,2.0) < 1.0 ? -iTime : iTime;

    return fract(_st);
}
vec2 brickTileVertical(vec2 _st, float _zoom){
    _st *= _zoom;

    // Here is where the offset is happening
    _st.y += mod(_st.x,2.0) < 1.0 ? -iTime : iTime;

    return fract(_st);
}
float box(vec2 _st, vec2 _size){
    _size = vec2(0.5)-_size*0.5;
    vec2 uv = smoothstep(_size,_size+vec2(1e-4),_st);
    uv *= smoothstep(_size,_size+vec2(1e-4),vec2(1.0)-_st);
    return uv.x*uv.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 st = fragCoord/iResolution.xy;
    vec3 color = vec3(0.0);
    st = mod(floor(iTime),2.)==1. ?  brickTileHorizontal(st,5.0) : brickTileVertical(st,5.0);

    color = vec3(box(st,vec2(0.9)));

    fragColor = vec4(color,1.0);
}
