void mainImage( out vec4 O, vec2 u )
{
	vec2 U = u / iResolution.xy,
       eps = vec2(.005, 0),
         D = U; D.x = D.x*1.2 - .1;

    #define D(eps)  dot( textureLod(iChannel1,D + eps, 0.).rgb, \
                     vec3(.2126, .7152, .0722)  )   // LUMA
    vec2 valDeriv = vec2( D(eps.xy) - D(-eps.xy),
                          D(eps.yx) - D(-eps.yx)
                        );
    D = abs(D-.5);
    if ( max(D.x,D.y) <.5 )
        U -= .1 * normalize(1e-5+valDeriv)
                 * smoothstep(.0, .2, length(valDeriv) ) ;    

	O = texture(iChannel0, U);	
}
