void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);
    vec2 mv = .7*uv/vec2(abs(length(uv*.1)*abs(cos(uv.x)+.5))*-abs(sin(uv.x)*-10.),length(uv*.25));

    float blur = 1.2-abs(sin(length(mv))*.05);
    vec2 gv = mv+vec2(length(blur*mv))*1.;
    float dist = 1.+length(cos(mv.x*mv)*.5);
    float mask = smoothstep(blur, -blur, dist-length(gv+uv*.25)*.5)*.5;

    gv = 120.0*length(uv+cos(gv.y)-gv.y*cos(iTime)*gv*uv)*sin(.5+mod(mask,length(gv)))*(sin(uv));

    blur = .7/gv.x;
     col = vec3(mask)*-blur;
   	mask = blur*smoothstep(blur, -blur, length(gv)+blur);



    gv *= 1.5*blur*sin(mv*15.);


    dist *= length(7.*sin(-vec2(dist*.1)));
    mask = 1.-dist*smoothstep(blur, -blur, .9/length(gv-uv*-.25)*.5)*.25;

    dist = 2.5*length(uv+mask)*mask;


     col /= (vec3(mask*(sin(mask))*blur)*.8);//*vec3(0.5,0.5,0.5);

     col *= mask;

    col *= (2./(1.5+vec3(length(uv))))*.8;
    col *= (2./vec3(1.5/blur/dist))*.8;



    fragColor = vec4(col,1.0);
}
