void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Output to screen
    fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0) + vec4(-0.1);
    //if (fragColor.r < -0.8) fragColor = vec4(0.5,0.5,0.6,1.0);
    int i = int(fragColor.r*10000.0) % 881;
    if (i > 850) fragColor = vec4(0.8,0.7,0.1,1.0);
    if (fragColor.r > 1.0) fragColor = vec4(0.2,0.0,0.6,1.0);

    vec2 txcoord = fragCoord / iResolution.y;
    const float lens = 0.5;
    txcoord.x += fragColor.g * lens;
    txcoord.y += fragColor.b * lens;
    fragColor += texture(iChannel2, txcoord, 0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    const float damp = 0.9995;

    fragColor = (texelFetch(iChannel0, ivec2(fragCoord), 0) +
                 texelFetch(iChannel1, ivec2(fragCoord), 0)) * damp;

    //fragColor = vec4(0.0,0.0,0.0,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float sz = 0.003;
    const float damp = 0.9995;
    const vec4 samef = vec4(-1.00005, -1.00015, -1.0002, 1.0);
    const float otherf = +1.00 * 0.25;


    vec2 d = fragCoord-iMouse.xy;
    float c = 0.01*(1.0-clamp(dot(d, d)*sz, 0.0, 1.0))*clamp(iMouse.z, 0.0, 1.0);

    vec4 acc = texelFetch(iChannel0, ivec2(fragCoord), 0) * samef + 
               (texelFetch(iChannel0, ivec2(fragCoord.x, fragCoord.y + 1.0), 0) +
                texelFetch(iChannel0, ivec2(fragCoord.x, fragCoord.y - 1.0), 0) +
                texelFetch(iChannel0, ivec2(fragCoord.x + 1.0, fragCoord.y), 0) +
                texelFetch(iChannel0, ivec2(fragCoord.x - 1.0, fragCoord.y), 0)
               ) * otherf;

    fragColor = (vec4(c,c,c,1.0) +
                texelFetch(iChannel1, ivec2(fragCoord), 0) +
                acc
                ) * damp;

   //fragColor = vec4(0.0,0.0,0.0,1.0);
}
