void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float frequency = 0.05;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float c1 = round(abs(sin(fragCoord.x * frequency)) - 0.25);
    float c2 = round(abs(sin((fragCoord.y  + (c1 * 100.0 * sin(iTime))) * frequency))-0.25);

    vec3 col1 = vec3(c2, sin(uv.x + iTime),cos(uv.y + iTime));

    // Output to screen
    fragColor = vec4(col1,1.0);
}
