float star(vec2 uv) {
    return sin(pow((1.-dot(vec2(0.5,0.5), abs(uv))), sin(iTime / 5.) * 10.));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy-.5;
    fragColor = vec4(1.-star(uv),abs(star(uv))-sin(iTime),sin(iTime)-abs(star(uv)), 1.);
}
