void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy * 2.0 -iResolution.xy) / min(iResolution.x, iResolution.y);
    vec3 col = vec3(0.);
    for(float r,l,i = 0. ; i < 234. ; i+= 1.)
    {
        l = length(p + cos(length(p) ));
        col += max( 0.,(1.+sin( sin(min(p.x , p.y)) ) ))/36. * vec3(1.,.5,4.7)/3. * abs(log(l));
        r = floor(  + sin(l * 15. + iTime) );
        p *= mat2(cos(r),sin( r),-sin(r * 1.7),cos(r * .1));
    }
    col = log(20. * col) * col/10.;
    fragColor = vec4(col,1.0);
}
