// Inspiration from https://thebookofshaders.com/edit.php#10/ikeda-00.frag
float random(vec2 pos, float mod_time) {
  float A = 30.0;
  float w = 0.0200;
  float phi = 0.0;
  float wrap_time = A * cos(w * mod_time + phi);
  wrap_time = -abs(wrap_time);
  float arg0 = 12.9898;
  float arg1 = 780.2333 / 10000.0 * log(mod_time);
  float arg2 = 143758.5453123 / (10.0 * wrap_time);
  return fract(sin(dot(pos.xy, vec2(arg0, arg1))) * arg2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
  vec2 pos = fragCoord.xy / iResolution.xy;
  pos.x *= 0.25;
  pos.y *= 0.05;

  vec3 color = vec3(0.0);
  float time_limit = 160.0;
  float mod_time = mod(iTime, time_limit);


  float rnd = random( pos, mod_time ) * 0.4;
  //color = vec3(cos(rnd), sin(rnd), atan(rnd));
  color = vec3(tan(rnd), log(rnd), 1.0 - sqrt(rnd));

  fragColor = vec4(color, 1.0);
}
