// Perlin noise variant of "metafall" https://shadertoy.com/view/wllGzB

#define hash(p) ( 2.* fract(sin( (p) * mat2(127.1,311.7,269.5,183.3))*43758.5453123) -1.)

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = u / R.y, V = 15.*U; V.y += iTime;

    vec2 C, v = smoothstep(0.,1.,fract(V)); // Perlin noise
#define Px(x,y) dot( hash( C = floor(V) + vec2(x,y) ), V-C )
#define Py(y)   mix( Px(0,y), Px(1,y), v.x)
    float p =   mix( Py(  0), Py(  1), v.y);
    p = .55+.75*p;

  //p = sin(10.*p); 
    O = vec4( (.2+.7*U.y-p) / fwidth(p) ); // * vec4(.5,.7,1.2,1);
}
