void mainImage( out vec4 O, vec2 U )
{
    O = vec4( T(U).x > U.y/R.y *T(0).w );             // histogramm

    U -= .5;
    if ( U.x == floor(60.*R.x/scale)) O.gb *= 0.;     // red line at 60 fps

    if ( floor(U.y/4.) == floor(R.y/8.) ) O.g += float(mod(U.x+1.,10.*R.x/scale) < 1.); // green tick every 10 fps
}

void mainImage( out vec4 O, vec2 U )
{
    O = T(U);                               // prev count

    if (int(N*scale*U.x/R.x) == int(N/iTimeDelta)) O++; // histograming

    if (U==vec2(.5)) {                      // max value in (0,0)
        float M = 0.;
        for (float x=0.; x < R.x; x++)
            M = max(M,T(vec2(x,1)).x);
        O.w = M;
    }
}
