float func(float x){
 return x*x;   
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 uv = 2.0*vec2( fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;


    vec3 backgroundColor = vec3(1.0);
    vec3 funcColor = vec3(0.0, 1.0, 0.0);
    vec3 lineColor = vec3(0.0,0.0,0.0);

    vec3 pixel = backgroundColor;

    const float dl = 0.1;
    const float w = 0.001;
    for(float i = -10.0; i <= 10.0; i+=dl){
     	if(abs(uv.x - i) <= w)
            pixel = lineColor;
        if(abs(uv.y - i) <= w)
            pixel = lineColor;
    }

    float x = uv.x;
    float y = uv.y;
    float v = 5.0;
    if(abs(func(x*v)/v - y) < 0.01){
        pixel = funcColor;
    }

    fragColor = vec4(pixel, 1.0);
}
