void mainImage( out vec4 o, in vec2 FC )
{
    vec2 p=(2.*FC-iResolution.xy)/iResolution.y;
    vec3 c=vec3(.5-.9*sin(iTime*.5));
    for(float i=-5.;i<9.;i++)
        p*=1.1,
        c+=sin(iTime*.5)
        *vec3(.5,.5+.5*cos(iTime),.5+.5*sin(iTime))
        *(1.-smoothstep(.1,.2,abs(length(p)-.5)))
        *smoothstep(-1.,.5,sin(floor(cos(iTime/4.)*16.)*atan(p.y,p.x)));
    o=vec4(c,1.);
}
