vec2 pa(in vec2 uv, in float per){
    vec2 result = (sin(uv.x*per)) * normalize(vec2(cos((uv.x)*per), .5));
    return result;
}

vec2 pb(in vec2 uv, in float per){
    vec2 result = (cos(uv.y*per)) * normalize(vec2(1., cos((uv.y)*per)));
    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime*.4;

	vec2 uv = -1. + .1*(fragCoord.xy / iResolution.xy);
    uv.x *= iResolution.x/iResolution.y*.5+.5;

    vec2 vpert = pa(uv, 20.);
    uv += vpert * sin(t) *.2;

    vec2 hpert = pb(uv, 51.);
     uv += hpert * sin(t) * .08;

    //vec4 col = texture(iChannel0, uv);
    //fragColor = col;
	fragColor = mix(6.*vec4(sin(uv.y*12.),mod(uv.x*12.,.25), 
    cos(uv.y*13.), 1.), vec4(sin(t*.5)*3.,sin(t*.5),sin(t*1.5)*11.,
    tan(t*22.15)*1.), .249);
     //fragColor *= col;
}
