float Random2d(vec2 coord)
{
    // D'apr√®s https://www.youtube.com/watch?v=nM320eVlLvQ
    return fract(sin(dot(coord.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}


vec3 colNoise(vec2 st, vec3 c, float amp) {
  float x = Random2d(st) * amp;
  float y = Random2d(st) * (amp-x);
  float z = Random2d(st) * (amp-x-y);
  x -= amp/2.;
  y-=amp/2.;
  z-=amp/2.;
  float tmp;
  // Shuffle
  for (int i=0; i<3; i++) {
    switch (int(floor(Random2d(st) * 3.))) {
      case 0: tmp=x; x=y; y=tmp;
              break;
      case 1: tmp=x; x=z; z=tmp;
              break;
      case 2: tmp=y; y=z; z=tmp;
              break;
      default: break;
    }
  }
  //return vec3(red(c)+x, green(c)+y, blue(c)+z);
    return c + vec3(x,y,z);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy;
    st *= 2. + 4. * (1. + sin(iTime/10.));
    st.x *= iResolution.x/iResolution.y;
    st.x += iTime/10.;

    vec3 color = vec3(0.9);
    float rnd = .0;

    int i = 0;

    while (rnd < .9 && i < 4) {
        st *= 1.8 + 1.1 * cos(iTime*0.06);
        vec2 zoneCoord = floor(st.xy);
        rnd = Random2d(zoneCoord);
    	i++;
    }
    color = vec3(rnd);

    fragColor = vec4(color, 1.0);
}
