float zoom = 1.1;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = (uv-.5)*2.;
    uv *= zoom;

    uv.x *= 1. + pow(abs(uv.y/2.),3.);
    uv.y *= 1. + pow(abs(uv.x/2.),3.);
    uv = (uv + 1.)*.5;

    vec4 tex = vec4( 
        texture(iChannel0,uv+.004).r,
        texture(iChannel0,uv).g,
        texture(iChannel0,uv-.004).b, 1.
    );
                 //little thin lines            //big thick lines
    float lines = 0.01*cos(uv.y*iResolution.y) + 0.1*sin(uv.y*20. + iTime);
    tex.rgb += lines;

    //wkrmfwkrmfgo
    //tex *= step(uv.x,1.)*step(uv.y,1.)*step(0.,uv.x)*step(0.,uv.y);

    //smooth ver
    tex *= smoothstep(uv.x,uv.x+0.01,1.)*smoothstep(uv.y,uv.y+0.01,1.)*smoothstep(-0.01,0.,uv.x)*smoothstep(-0.01,0.,uv.y);

    float avg = (tex.r+tex.g+tex.b)/3.;
    fragColor = tex + pow(avg,3.);
}
