void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 cam = texture(iChannel0, uv);
#if 0
    vec4 bkg = texture(iChannel1, uv);
#else
    vec4 bkg = vec4(0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4)), 1.0);
#endif

    vec4 result = mix(cam, bkg, cam.a);

    fragColor = result;
}

float alpha(vec3 a, vec3 b) {
    const float k0 = 0.15, k1 = 0.75;

    a = vec3(dot(a, vec3(0.299, 0.587, 0.114)),
             dot(a, vec3(-0.14713, -0.28886, 0.436)),
             dot(a, vec3(0.615, -0.51499, -0.10001)));
    b = vec3(dot(b, vec3(0.299, 0.587, 0.114)),
             dot(b, vec3(-0.14713, -0.28886, 0.436)),
             dot(b, vec3(0.615, -0.51499, -0.10001)));
    a.x *= k1;
    b.x *= k1;
    return 1.0 - smoothstep(k0 * 0.9, k0, distance(a, b));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 cam = texture(iChannel0, uv).xyz;
    vec3 bkg = texture(iChannel1, uv).xyz;
    fragColor = vec4(cam, alpha(cam, bkg));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 cam = texture(iChannel0, uv);
    vec4 bkg = texture(iChannel1, uv);
    vec4 blend = mix(cam, bkg, 0.998);
    blend = mix(bkg, blend, cam.a);
    if (iMouse.z > 0.0) blend = cam;
    fragColor = blend;
}
