void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iMouse.xy)/iResolution.y;
	vec3 col = vec3(0);

    uv *= 30.;

    vec2 gv = fract(uv) - 0.5;
	vec2 id = floor(uv);
    uv *= abs(gv.y);

    gv *= abs(uv.x);
    gv += sin(iTime * id) - cos(iTime);

    float d = length(gv);
    float d2 = length(uv);

    float m = abs(sin(iTime * 1.0) * .5) / d;

    col += m;

    col *= vec3(.5, abs(sin(iTime + 200.)), abs(sin(iTime + 50.)));

    fragColor = vec4(col, 1.0);
}
