#define grad(x) length(vec2(dFdx(x),dFdy(x)))
#define PI 3.14159265359

float squircle(in vec2 pos, float n) {
    vec2 tmp = pow(abs(pos),vec2(n));
    return 1.-dot(vec2(1.0, 1.0), tmp);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 scale = 1./iResolution.xy;
    vec2 uv = (fragCoord*scale - vec2(0.5));
    uv *= vec2(iResolution.x/iResolution.y,1.) * 3.;

    float ct = cos(iTime);
    float st = sin(iTime);

    mat2 rot = mat2(ct, st, -st, ct);

    vec2 uv2 = rot * uv;
    float n = mix(0.1,10.,sin(iTime*2. + PI*0.5) * .5 + .5);
    float s = squircle(uv2,n);
    float a;
    bool b = (iMouse.w > 0.5 && iMouse.x > fragCoord.x) ||
        (iMouse.w < 0.5 && fragCoord.x < iResolution.x*0.5);
    if ( b ){
    	a = s;
    } else {
        a = abs(s)/grad(s);
    }
    fragColor = vec4(a);
}
