void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;
    uv.xy *= 5.0;
    /*vec2 center = vec2(
        iResolution.x * 0.5, iResolution.y * 0.5);*/
	vec2 center = vec2(iMouse.x, iMouse.y);
    // Time varying pixel color
    float dist = distance(center,fragCoord) / 5.0;
    float red = 2.0 * sin(iTime - dist);
    float blue = 2.0 * cos(iMouse.y + dist);
    float green = 4.0 * tan(iTime - dist);
    vec3 col = vec3(red,green,blue);
    vec3 col2 = vec3(0,6,12);
    //vec3 col2 = 0.5 + 0.5 * tan(iTime + uv.xxy+vec3(0,6,12));

    // Output to screen
	fragColor = vec4(col,1.0);
}
