float cubicPulse( float c, float w, float x ){
    x = abs(x - c);
    if( x>w ) return 0.0;
    x /= w;
    return 1.0 - x*x*(3.0-2.0*x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord / iResolution.xy;
    vec2 m = iMouse.xy / iResolution.xy;
    float wave = iTime+uv.x*(5.+10.*m.x);
    float c = .5+.5*sin(wave);
    float w = .05;
    float y = uv.y*(5.+10.*m.y);
    float prevPos = fract(y)-1.;
    float thisPos = fract(y);
    float nextPos = fract(y)+1.;
    vec3 picLondon = texture(iChannel0, uv).rgb;
    float br = length(picLondon);
    w += br*.35;
    float gray = max(max(
        cubicPulse(c, w, prevPos), 
        cubicPulse(c, w, thisPos)),
        cubicPulse(c, w, nextPos)); 
    vec3 col = vec3(gray);
    fragColor = vec4(col,1.0);
}
