#define MAX_RAY_MARCH_STEPS 100
#define MAX_RAY_MARCH_DISTANCE 100.0
#define MIN_SURFACE_DISTANCE 0.01


// list of (perhaps) useful colors
const vec3 red_rgb     = vec3(1.0,0.0,0.0);
const vec3 green_rgb   = vec3(0.0,1.0,0.0);
const vec3 blue_rgb    = vec3(0.0,0.0,1.0);
const vec3 white_rgb   = vec3(1.0,1.0,1.0);
const vec3 black_rgb   = vec3(0.0,0.0,0.0);
const vec3 gray_rgb    = vec3(0.1,0.1,0.1);  //vec3(0.5,0.5,0.5);
const vec3 cyan_rgb    = vec3(0.0,1.0,1.0);
const vec3 magenta_rgb = vec3(1.0,0.0,1.0);
const vec3 maroon_rgb  = vec3(0.5,0.0,0.0);
const vec3 olive_rgb   = vec3(0.0,0.1,0.0);  //vec3(85./255.,107./255.,47./255.);
const vec3 purple_rgb  = vec3(0.5,0.0,0.5);
const vec3 teal_rgb    = vec3(0.0,0.5,0.5);
const vec3 violet_rgb  = vec3(0.1,0.0,0.2);
const vec3 yellow_rgb  = vec3(1.0,1.0,0.0);


// Specify the colors for the cube and sphere.
const vec3 cube_color = blue_rgb;
const vec3 sphere_color = red_rgb;
const vec3 squareCircle_color = teal_rgb;
const vec3 circleSquare_color = purple_rgb;
const vec3 plane_color = violet_rgb;
const vec3 cube123_color = olive_rgb;



// maybe handy axis definitions
#define xIndex 0
#define yIndex 1
#define zIndex 2
#define aIndex 3
#define bIndex 4
#define cIndex 5



// cube in nD (n dimensions)
float sdfCubenD (in float[nD] currentRay, in float sideLength)
{
    return maximum(absnD(currentRay)) - sideLength;   
}


// cube in dimensions 1, 2, 3
float sdfCube123(in float[nD] currentRay, in float sideLength)
{
    float[nD] tempnD = absnD(currentRay);
    return (max(tempnD[0], max(tempnD[1], tempnD[2]))) - sideLength; 
}


// Sphere, in n dimensions, signed distance functions (sdf)
float sdfSpherenD(in float[nD] currentRay, in float radius, in float[nD]sphereLocation)
{
        return lengthnD(minus(currentRay,sphereLocation)) - radius;
}

// XZ plane signed distance functions (sdf)
float sdfPlaneXZ(in float[nD] currentRay)
{
    return currentRay[yIndex];
}


// XZ plane signed distance functions (sdf)
float sdfPlaneAC(in float[nD] currentRay)
{
    return currentRay[bIndex];
}



/*
// XZ plane signed distance functions (sdf)
float sdfPlanesXZAC(in float[nD] currentRay)
{
    float currentDistance = currentRay[yIndex];

    if(currentRay[bIndex] > currentRay[yIndex]){currentDistance = currentRay[bIndex];}

    return currentDistance;
}
