//
// Compute the distanced from the objects in the scene.
//
vec4 getDistanceAndColor(float[nD] currentRay)
{  
    vec3 currentColor = vec3(0.0);  // default to black
    float currentDistance = MAX_RAY_MARCH_DISTANCE;  // default to far away


    // sphere at location x, y, z, and radius 1
    float[nD] sphereLocation = values(0.0);
    sphereLocation[0] = 0.0;  sphereLocation[1] = 1.0;  sphereLocation[2] = 4.0;
    float sphereRadius = 1.0;

    // distnace to the sphere
    float sphereDistance = sdfSpherenD(currentRay, sphereRadius, sphereLocation);

    // Replace current distance and color if current object is closer.
    if(sphereDistance<currentDistance)
    {
        currentDistance = sphereDistance;
        currentColor = sphere_color;
    }


    // distance to the cube (in dims 1 2 3 only)
    float sideLength = 0.5;
    //float objectDistance = sdfCube123(currentRay, sideLength);

    // distance to the cube, n dimensional version
    float objectDistance = sdfCubenD(currentRay, sideLength);

    // Replace current distance and color if current object is closer.
	if(objectDistance < currentDistance)
    {
        currentDistance = objectDistance;
        currentColor = cube123_color;
    }


    // distance to the plane
    // just the height of the camera above the plane
    //float planeDistance = sdfPlaneXZ(currentRay);

    float planeDistance = sdfPlaneXZ(currentRay);

    // Replace current distance and color if current object is closer.
    if(planeDistance<currentDistance)
    {
        currentDistance = planeDistance;
        currentColor = plane_color;
    }

    vec4 localPixel = vec4(currentDistance, currentColor);

    return localPixel;
}


//
// basic ray marching (aka trace) routine
//
vec4 rayMarch(float[nD] rayOrigin, float[nD] rayDirection)
{
    float distanceFromOrigin = 0.0;
    vec4 currentPixel;  // packet of distance and color

    	for(int i=0; i<MAX_RAY_MARCH_STEPS; i++)
        {
            float[nD] currentRay = plus(rayOrigin, times(rayDirection, distanceFromOrigin));

            currentPixel = getDistanceAndColor(currentRay);
            float currentDistance = currentPixel.x;  // distance is the 1st value of the packet

            distanceFromOrigin += currentDistance;

            if( (distanceFromOrigin > MAX_RAY_MARCH_DISTANCE) || (currentDistance < MIN_SURFACE_DISTANCE) ) break;
        }

    currentPixel.x = distanceFromOrigin;

    return currentPixel;
}



//
// Approximate a normal vector at a point on an object.
//

float[nD] getNormal(float[nD] point)
{
    float[nD] nDtemp;
    float[nD] result;    
    float epsilon = 0.01;

    for(int i=0; i<nD; i++)
    {	
        nDtemp = values(0.0);
        nDtemp[i] = epsilon;

        // quick (difference) approximation, not currently used
        //result[i] = map(minus(point, nDtemp)).x;

        // another (difference) approximation
        // https://iquilezles.org/articles/normalsSDF
        result[i] = getDistanceAndColor(plus(point, nDtemp))[0] - getDistanceAndColor(minus(point, nDtemp))[0];
    }
    return normalizenD(result);
}



//
// part of shadow calculations
//
float getLight(float[nD] objectPoint)
{    
    // define a light above and away from the scene
    float[nD] lightPosition = vec2floatnD(vec3(0,5,6));

    // Move the light.
    // lightPosition.xz += 2.0 * vec2(sin(iTime), cos(iTime));

    float[nD] moveLight = values(0.0);
    moveLight[0] = 2.0*sin(iTime);
    moveLight[2] = 2.0*cos(iTime);

//    moveLight[3] = 2.0*sin(iTime);
//    moveLight[5] = 2.0*cos(iTime);

    lightPosition = plus(lightPosition, moveLight);

    // difuse lighting = lightDirection DOT localNormal
    float[nD] lightDirection = normalizenD(minus(lightPosition, objectPoint));    

    float[nD] localNormal = getNormal(objectPoint);

    float difuseLighting = clamp(dotnD(localNormal, lightDirection), 0.0, 1.0);

    //
    // Compute a simple shadow.
    //
    // Ray march from the object toward the light.
    // If another object is encountered before the light,
    // then this point must be in a shadow.
    float fudgeFactor = 2.0;  // eliminates a flaw on the sphere surface
    // vec3 justAboveObject = objectPoint + fudgeFactor*localNormal*MIN_SURFACE_DISTANCE;
    float[nD] justAboveObject = plus(objectPoint, times(localNormal, fudgeFactor*MIN_SURFACE_DISTANCE));

    vec4 localPixel = rayMarch(justAboveObject, lightDirection);

    float distanceTowardLight = localPixel[0];  // distance is in the 1st slot

    // If point is in shadow, shadow = 10% of light.
    if(distanceTowardLight < lengthnD(minus(lightPosition, objectPoint))) difuseLighting *= 0.1;  

    return difuseLighting;
}



//
// frag shader
//
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates 
    vec2 uv = (fragCoord-0.5*iResolution.xy) / iResolution.y;

	// Start with a black pixel.
    vec3 color = vec3(0.0);

    // trivial camera
    float[nD] rayOrigin = vec2floatnD(vec3(0.0,1.0,-6.0), values(0.0));
    float[nD] rayDirection = normalizenD(vec2floatnD(vec3(uv.x, uv.y, 1.0), values(0.0)));

    //
    //
    // Rotate the origin and direction vectors.
    // The rotation is defined by rotating from an original origin and direction
    // by a rotation matrix using 2 principle axes, eg axis 2 and axis 4. 
    //
    //invoke by:  getRotationMatrix(in int axis1, in int axis2, in float theta)
    float angle = iTime / 3.0;
    matnD rotationMatrix = getRotationMatrix(2,4, angle);  //(0,2, angle);
    rayOrigin = times(rotationMatrix, rayOrigin);
    rayDirection = times(rotationMatrix, rayDirection);


    // Ray march and set the color.
    vec4 objectDistanceAndColor = rayMarch(rayOrigin, rayDirection);
    color = vec3(objectDistanceAndColor[0]/6.0);

    // Compute a vector to the object encountered in the ray march.
    float[nD] objectPoint = plus(rayOrigin, times(rayDirection, objectDistanceAndColor[0]));

    float difuseLighting = getLight(objectPoint);

    // test only:  color = getNormal(objectPoint);

    color = vec3(difuseLighting) * objectDistanceAndColor.yzw ;

    // gamma correction
    color = pow(color, vec3(0.45445));

    // Output to screen
    fragColor = vec4(color,1.0);
}

//
//
//  This is a toy code to explore N dimensional frag shading.
//
//  Below are definitions to extend a data types to include
//  vectors of length nD,
//  intended for n dimensional ray marching, and other uses.
//
//  The full spec of glsl already contains these vectors,
//  but implementing a full spec is rare, and perhaps even ill advised.
//
//  Caution:  one can cause runtime errors by defining nD to be below 4.
//
//  In my defense, I was left unsupervised.
//
//
