// number of dimensions
#define nD 6

// a handy shorcut...
#define fornD for(int i=0; i<nD; i++)

// whether to do compile time "tests"
#define doVecnDMatnDCompileTimeTests 0


//
//
// list of n dimensional functions defined herein:
//
//		RETURN		NAME		 INPUT TYPE(S)
//		 TYPE
//
//		float[nD] 	values		 	 (in float value)
//		float      	sum    	  	 	 (in float[nD] vector)
//		float[nD]  	absnD	  	 	 (in float[nD] vector)
//		float[nD]  	plus	 	 	 (in float[nD] vector1, in float[nD] vector2)
//		float[nD] 	minus		 	 (in float[nD] vector1, in float[nD] vector2)
//		float[nD]  	times	  	 	 (in float scalar, in float[nD] vector)
//		float[nD]  	times	  	 	 (in float[nD] vector, in float scalar)
//		float[nD] 	times		 	 (matnD matrix, float[nD] vector)
//		float      	dotnD	 	  	 	 (in float[nD] vector1, in float[nD] vector2)
//		float      	lengthnD 	  	 	 (in float[nD] vector)
//		float 	   	L2norm	  	 	 (in float[nD] vector)
//		float 	   	L1norm	  	 	 (in float[nD] vector)
//		float[nD]  	normalizenD  	 	 (in float[nD] vector)
//		float[nD] 	fractnD		 	 (in float[nD] vector)
//		float 		maximum		 	 (in float[nD] vector)
//		float[nD] 	maximum		 	 (in float[nD] vector1, in float[nD] vector2)
//		float 		maximum		 	 (in vec3 vec)
//		float 		minimum		 	 (in float[nD] vector)
//		float[nD] 	minimum		 	 (in float[nD] vector1, in float[nD] vector2)
//		vec2 		floatnD2vec2 	 (in float[nD] vector)
//		vec3 		floatnD2vec3 	 (in float[nD] vector)
//		vec4 		floatnD2vec4 	 (in float[nD] vector)
//		float[nD] 	vec2floatnD	 	 (in vec2 vec)
//		float[nD] 	vec2floatnD	 	 (in vec2 vec, in float[nD] vector)
//		float[nD] 	vec2floatnD  	 (in vec3 vec)
//		float[nD] 	vec2floatnD	 	 (in vec3 vec, in float[nD] vector)
//		float[nD] 	vec2floatnD  	 (in vec4 vec)
//		float[nD] 	vec2floatnD	 	 (in vec4 vec, in float[nD] vector)
//
//		vecnD		struct vecnD	 ()
//		float[nD]	vecnD2floatnD	 (in vecnD vector)
//		matnD 		struct matnD 	 ()
//		matnD 		values		 	 (in float value, in matnD dummy)
//		matnD 		identity	 	 ()
//		matnD 		identity	 	 (matnD dummy)
//		matnD 		getRotationMatrix(in int axis1, in int axis2, in float theta)
//		float[nD] 	times			 (in matnD matrix, in float[nD] vector)
//
//
//
// unfinished		float 		doDeterminant(in vecnD[nD] matrix)
//
//



//
// Set all elements of a float[nD] to a specific value.
//
float[nD] values(in float value)
{
    float[nD] result;
    for(int i=0; i<nD; i++) {result[i] = value;}
    return result;
}


//
// Sum the elements of a vector to a grand total.
//
float sum(in float[nD] vector)
{
    float result;
    for(int i=0; i<nD; i++) {result += vector[i];}
    return result;
}


//
// Return a vector of elementwise absolute values.
//
float[nD] absnD(in float[nD] vector)
{
    float[nD] result;
    for(int i=0; i<nD; i++) {result[i] = abs(vector[i]);}
    return result;
}


//
// Return a vector of the elementwise sum of 2 vectors.
//
float[nD] plus(in float[nD] vector1, in float[nD] vector2)
{
    float[nD] result;
    for(int i=0; i<nD; i++) {result[i] = vector1[i] + vector2[i];}
    return result;
}
// Return elementwise sum of a vector element and a scalar.
float[nD] plus(in float[nD] vector, in float scalar)
{
    float[nD] result;
    for(int i=0; i<nD; i++) {result[i] = vector[i] + scalar;}
    return result;
}
// Return elementwise sum of a vector element and a scalar.
float[nD] plus(in float scalar, in float[nD] vector) {return plus(vector, scalar);}


//
// Return a vector of the elementwise difference of 2 vectors.
//
float[nD] minus(in float[nD] vector1, in float[nD] vector2)
{
    float[nD] result;
    for(int i=0; i<nD; i++) {result[i] = vector1[i] - vector2[i];}
    return result;
}
// Return elementwise difference of a vector element and a scalar.
float[nD] minus(in float[nD] vector, in float scalar)
{
    float[nD] result;
    for(int i=0; i<nD; i++) {result[i] = vector[i] - scalar;}
    return result;
}
// Return elementwise difference of a scalar element and a vector.
float[nD] minus(in float scalar, in float[nD] vector)
{
    float[nD] result;
    for(int i=0; i<nD; i++) {result[i] = scalar - vector[i];}
    return result;
}


//
// Product of a scalar times elements of a vector.
//
float[nD] times(in float scalar, in float[nD] vector)
{
    float[nD] result;
    for(int i=0; i<nD; i++) {result[i] = scalar * vector[i];}
    return result;
}
// reverse the operands
float[nD] times(in float[nD] vector, in float scalar) {return times(scalar, vector);}


//
// Dot product of 2 vectors.
//
float dotnD(in float[nD] vector1, in float[nD] vector2)
{
    float result = 0.0;
    for(int i=0; i<nD; i++) {result += (vector1[i] * vector2[i]);}
    return result;
}


//
// L2 norm of a vector.
//
float lengthnD(in float[nD] vector) {return sqrt(dotnD(vector,vector));}
//
// Alternate call to same function...
//
float L2norm(in float[nD] vector) {return lengthnD(vector);}


//
// L1 norm of a vector, 
// which is the sum of the absolute value of the individual elements.
//
float L1norm(in float[nD] vector) {return sum(absnD(vector));}


//
// Normalize an nD vector.
//
float[nD] normalizenD(in float[nD] vector) {return times(1.0/lengthnD(vector), vector);}


//
// Do an elementwise fractional part of number, on a vector.
// Computed as:  x - floor(x), elementwise.
//
float[nD] fractnD(in float[nD] vector)
{
    float[nD] result;
    for(int i=0; i<nD; i++) {result[i] = fract(vector[i]);}
    return result;
}


//
// Find the maximum across all elements of a float[nD].
//
float maximum(in float[nD] vector)
{
    float result = vector[0];  // get an initial "maximum"
    for(int i=1; i<nD; i++) {result = max(result,vector[i]);}
    return result;
}
// Elementwise maximum between 2 float[nD] vectors
float[nD] maximum(in float[nD] vector1, in float[nD] vector2)
{
    float[nD] result;
    for(int i=0; i<nD; i++) {result[i] = max(vector1[i], vector2[i]);}
    return result;
}
// Elementwise maximum between a float[nD] and a scalar.
float[nD] maximum(in float[nD] vector, in float scalar)
{
    float[nD] result;
    for(int i=0; i<nD; i++) {result[i] = max(vector[i], scalar);}
    return result;
}
// Find the maximum across a vec3 vector.
float maximum(in vec3 vec) {return max(vec.x, max(vec.y, vec.z));}

//
// Find the maximum across all elements of a float[nD].
//
float minimum(in float[nD] vector)
{
    float result = vector[0];  // get an initial "maximum"
    for(int i=1; i<nD; i++) {result = min(result,vector[i]);}
    return result;
}
// Elementwise maximum between 2 vectors
float[nD] minimum(in float[nD] vector1, in float[nD] vector2)
{
    float[nD] result;
    for(int i=0; i<nD; i++) {result[i] = min(vector1[i], vector2[i]);}
    return result;
}


