//
// Convert a float[nD] to a vec2. Use only the 1st 2 elements.
//
vec2 floatnD2vec2(in float[nD] vector)
{
    vec2 result;
    for(int i=0; i<2; i++) {result[i] = vector[i];}
    return result;
}


//
// Convert a float[nD] to a vec3. Use only the 1st 3 elements.
//
vec3 floatnD2vec3(in float[nD] vector)
{
    vec3 result;
    for(int i=0; i<3; i++) {result[i] = vector[i];}
    return result;
}


//
// Convert a float[nD] to a vec3. Use only the 1st 4 elements.
//
vec4 floatnD2vec4(in float[nD] vector)
{
    vec4 result;
    for(int i=0; i<4; i++) {result[i] = vector[i];}
    return result;
}


//
// Convert a vec2 to a float[nD]. Only the 1st 2 elements are written,
// all other elements of the float[nD] are set to zero.
//
float[nD] vec2floatnD(in vec2 vec)
{
    float[nD] result = values(0.0);
    for(int i=0; i<2; i++) {result[i] = vec[i];}
    return result;
}
// Put a vec2 into the 1st elements of an existing float[nD],
// preserving the other elements of the float[nD].
float[nD] vec2floatnD(in vec2 vec, in float[nD] vector)
{
    float[nD] result = vector;
    for(int i=0; i<2; i++) {result[i] = vec[i];}
    return result;
}



//
// Convert a vec3 to a float[nD]. Only the 1st 3 elements are valid,
// all other elements 
//
float[nD] vec2floatnD(in vec3 vec)
{
    float[nD] result = values(0.0);
    for(int i=0; i<3; i++) {result[i] = vec[i];}
    return result;
}
// Put a vec3 into the 1st elements of an existing float[nD],
// preserving the other elements of the float[nD].
float[nD] vec2floatnD(in vec3 vec, in float[nD] vector)
{
    float[nD] result = vector;
    for(int i=0; i<3; i++) {result[i] = vec[i];}
    return result;
}



//
// Convert a vec4 to a float[nD]. Only the 1st 4 elements are valid,
// all other elements 
//
float[nD] vec2floatnD(in vec4 vec)
{
    float[nD] result = values(0.0);
    for(int i=0; i<4; i++) {result[i] = vec[i];}
    return result;
}
// Put a vec4 into the 1st elements of an existing float[nD],
// preserving the other elements of the float[nD].
float[nD] vec2floatnD(in vec4 vec, in float[nD] vector)
{
    float[nD] result = vector;
    for(int i=0; i<4; i++) {result[i] = vec[i];}
    return result;
}



//
// Define an explicit vector type of length nD.
// This is useful for defining nD x nD matricies
//
// This structure should generall be avoided in favor
// of using:  float[nD]
//
// This stuff is needed for the clumsy matrix handling.
//
struct vecnD 
{
    float[nD] col;  // short for element
};


//
// Define an explicit type of nD x nD matrix.
// This is useful for determinants, and the like.
//
// This structure should generall be avoided.
//
struct matnD
{
    vecnD[nD] row;  // short for element
};


//
// Convert a vecnD to a float[nD].
// This stuff is needed for the clumsy matrix handling.
//    
float[nD] vecnD2floatnD(in vecnD vector)
{
    float[nD] result;
    for(int i=0; i<nD; i++){result[i] = vector.col[i];}
    return result;
}


//
// Convert a float[nD] to a vecnD.
// This stuff is needed for the clumsy matrix handling.
//    
vecnD floatnD2vecnD(in float[nD] vector)
{
    vecnD result;
    for(int i=0; i<nD; i++){result.col[i] = vector[i];}
    return result;
}



//
// Fill a matrix with a float value.
// The dummy is only included so the function 'values'
// can identify the type to return.
//    
matnD values(in float value, in matnD dummy)    
{
    matnD result;
    for(int i; i<nD; i++)
    {
        for(int j; j<nD; j++)
        {
            result.row[i].col[j] = value;
        }
    }
    return result;
}



//
// Return an identity matrix, ie a zero matrix with 1 on the diagonal. 
//
matnD identity()
{
    matnD result;
    result = values(0.0, result);

    for(int i=0; i<nD; i++)
    {
        result.row[i].col[i] = 1.0;
    }
    return result;
}
matnD identity(in matnD dummy)
{
    matnD result;
    result = values(0.0, result);

    for(int i=0; i<nD; i++)
    {
        result.row[i].col[i] = 1.0;
    }
    return result;
}


//
// Construct a rotation matrix around a principle axis, starting with dimension 0. Specify the axis number,
// and the angle, theta.
//
matnD getRotationMatrix(in int axis1, in int axis2, in float theta)
{
    // reverse the axes, if axis2 is smaller.
    if(axis2 < axis1) {int temp = axis1; axis1 = axis2; axis2 = temp;}

    //
    // construct the rotation matrix.
    //
    // Make an identity matrix
    matnD rotation = identity();

    //
    // Set the rotation values.
    // 
    // 1st the diagonals: cos
    rotation.row[axis1].col[axis1] = cos(theta);
    rotation.row[axis2].col[axis2] = cos(theta);
    //
    // then the lower value:  sin
    rotation.row[abs(axis2)].col[axis1] = sin(theta);
    //
    // last the upper value:  -sin
    rotation.row[abs(axis1)].col[axis2] = -sin(theta);

    return rotation;
}




//
// Matrix times a vector:  Mv    ie vector on right
//
float[nD] times(in matnD matrix, in float[nD] vector)
{
    float[nD] result = values(0.0);

    for(int i=0; i<nD; i++) 
    {
        float[nD] currentRow = vecnD2floatnD(matrix.row[i]);
		result[i] = dotnD(currentRow, vector);
    }

    return result;
}
