void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    vec4 value = texture(iChannel0, uv);
    // Normalized pixel coordinates (from 0 to 1)

    float t = iTime * .00021;
    vec2 p = fragCoord.xy / iResolution.xy;// + vec2(0.15, 0.0);


    vec2 q = p - vec2(.5, .5);


    vec3 col = vec3(.6, .2, .4 );



       //float r =  - 100.04 * cos( atan( q.x, q.y) *  (100.00) );
    float r =  cos( atan( q.y, q.x) *  ( 1.5 *  sin(clamp(value.x, 0.0, 1.0))));

    col += smoothstep(r, r + value.x, length(q));

    // Output to screen
    fragColor = vec4(col,1.0);
}
