const mat2 m = mat2( 0.80,  0.60, -0.60,  0.80 );

float noise3( in vec2 p )
{
	return sin(p.x)*sin(p.y);
}

float fbm4( vec2 p ) //iq's
{
    float f = 0.0;
    f += 0.5000*noise3( p ); p = m*p*2.02;
    f += 0.2500*noise3( p ); p = m*p*2.03;
    f += 0.1250*noise3( p ); p = m*p*2.01;
    f += 0.0625*noise3( p );
    return f/0.9375;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //set the origin to the middle of the screen (thanks fabrice)
     vec2 st  = (fragCoord-.5*iResolution.xy) / iResolution.y;
 	//mirror effect
    st.x = .8 - abs(st.x);
    //flip vertically
    st.y= -st.y;

    //draw 3 circles 
    float channel1 = distance(st,vec2(0.3,.5))*3.;
    float channel2 = distance(st,vec2(0.2,.5))*3.;
    float channel3 =distance(st,vec2(0.5,.5))*3.;

    //combine into a vec3, animate
    vec3 colour= vec3(channel1,channel2,channel3)-(-iTime)/20.+iMouse.x/2000.;

    //uncomment line 38 and comment out line 38 to see this without fbm effect
    //									  *iMouse.y/200.
   colour = fract(colour*1.5+fbm4(st.xy)*12.)/vec3(0.31);
   //colour = fract(colour*1.5)/vec3(0.31);

    //this line gives it the green theme
    colour*=vec3(.5,.1,.5)*2.;
    //If the green is changed to brown, it can make a nice wood pattern

    // Output to screen
    fragColor = 1.-vec4(colour,1.0);
}
