void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv.y = fract(uv.y*3.);
    //uv.y -= .5;

    float T = iTime * 2.5;
    //T += uv.y*3.14*3.;
    //T = abs(fract(iTime)-.5);


    vec3 col = vec3(0);


    float t = uv.x;// + iTime * .2;
    float waves = 5.;
    float s = cos(t * 3.14*2. * waves);
    float h = mix(.0, .05, cos(T+3.14)*.5+.5);

    float s1 = s*h+h;
    float s2 = s*h-h;

    // triangle thick part
    float th = mix(.0, .5, sin(uv.x));

    //float R = (1.-step(s1+th, uv.y));
    //float G = (   step(s2-th, uv.y-1.));
    //float B = (   step(.5, uv.y));

    float a = 0.02; // aliasing
    float R = smoothstep(s1+th+a, s1+th, uv.y);
    float G = smoothstep(s2-th-a, s2-th, uv.y-1.);
    //float B1 = step(s*.1+.5 + uv.x, 1.-uv.y);
    //float B2 = step(s1 + .5, uv.y);

    //col = vec3(R);
    col = vec3(R+G);
    //col += step(s, uv.y);


    // Output to screen
    fragColor = vec4(col,1.0);
}
