void mainImage(out vec4 z, vec2 u) {
    for(
        vec2 A=u-u, C=z.xw, B=z.wx*1.7, G, i=A ;
        i.x++ < 13. ;
        C = G/3.
    )
        G = A+B,
        A = ( i.y = dot( normalize( u/6e2 -G), normalize(B+B-A))
            ) < -.5 ? A : B + C,
        B = i.y > .5 ? z = .5 - z, B : C,
        z = cos(iTime)+1. - z;
}
