vec2 sq(vec2 z, float sq_imag)
{
    return vec2(z.x * z.x + z.y * z.y * sq_imag, 2. * z.x * z.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy * 2. - 1.) * vec2(1., iResolution.y / iResolution.x);

    vec2 c = uv * 2. + vec2(0.5, 0.);

    float t = sin(iTime);

#define SAMPLES 64.

    vec2 z = c;
    float i = 0.;
    for(; i < SAMPLES; i += 1.)
    {
        z = sq(z, t) - c;
        if(length(z) > 2.)
            break;
    }

    fragColor = vec4(i / SAMPLES);
}
