void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int bailout = int(mod(iTime*3.,20.));
    vec2 uv = fragCoord/iResolution.xy;
    vec3 uvt = vec3(uv.x,uv.y,iTime*4.);
    vec3 uvt2 = uvt;
    for(int a = 0; a < bailout; a++){
        uvt2.x = cos(uvt.x*1.1+uvt.y*1.9+uvt.z*1.1);
        uvt2.y = cos(uvt.x*1.2+uvt.y*1.2+uvt.z*1.101);
        uvt2.z = cos(uvt.x*8.1+uvt.y*1.1+uvt.z*1.102);
        uvt = uvt2;
    }
    vec3 col = uvt/2.+.5;
    fragColor = vec4(col,1.0);
}
