void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(iFrame==0) {
    	fragColor = texture(iChannel0, fragCoord / iResolution.xy);
    } else {
        // read the previous output texture and use the zw
        // as the direction vector, normalise and scale and
        // add to the current position (.xy) then output
		vec4 mold = texture(iChannel1, fragCoord / iResolution.xy);
        vec2 dir1 = mold.zw;
        vec2 dir2 = ( ( normalize(dir1) * 2.0 ) - 1.0 ) * 0.01;
        mold.xy = fract(mold.xy + dir2.xy);
		fragColor = mold;
    }
}

