vec2 rot(vec2 p, float deg)
{
    mat2 rt = mat2(cos(deg),-sin(deg),sin(deg),cos(deg));
    return p*rt;
}

float noise(vec2 p)
{
    return fract(sin(p.x*256.34*iTime + p.y*513.54)*792.43);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col;

    float ang;
    for(float i=0.; i<10.; ++i)
    {
        float len = length(rot(uv,iTime) + (uv*i)-.5);
        ang = asin(0.001/len) * 20.;
		col += ang * vec3(.5,.5,noise(uv*i))*abs(sin(iTime));

    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
