#define scount 4
vec2 rotate(vec2 v, float a) {
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}
float rand(vec2 fragCoord) {

    return fract((sin(iTime*1777.555+fragCoord.x+fragCoord.y*iResolution.y)*10000.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 oldCol = texture(iChannel0,uv);
    //vec4 rand =  //texture(iChannel0,uv)*10000.)

    vec3 scan[scount];
    int hi = 0;
    for(int i=0; i<scount; i++) {
        float scanAng = 1.0/float(scount)*float(i)*6.2831;
        vec2 offset = rotate(vec2(.0,.005),scanAng);
        vec2 uvo = clamp(offset+uv,vec2(0,0),vec2(1,1));
        scan[i] = vec3(texture(iChannel0,uvo));
        if(scan[i].z > scan[hi].z) {
         	hi = i;
        }
    }

    if(iFrame < 6 || oldCol.x <= .1) {
        fragColor = vec4(rand(fragCoord),rand(fragCoord*1.00003),rand(fragCoord*1.00002),rand(fragCoord*1.00001))*texture(iChannel1,uv).xxxx+.1;
    } else {
		//fragColor = mix(oldCol*.99,vec4(scan[(iFrame/50)%scount],1),vec4(.05));
        fragColor = mix(oldCol*.99,vec4(scan[hi],1),vec4(.3));
    }

}
