//#define SHOW_NORMALS
//#define SHOW_SPECULAR
//#define SHOW_DIFFUSE

float getVal(in sampler2D img, vec2 uv) {
    return length(texture(img, uv).xyz);
}

vec2 getGrad(in sampler2D img, vec2 uv, float delta) {
    vec2 d=vec2(delta,0);
    return vec2(
        getVal(img, uv+d.xy)-getVal(img, uv-d.xy),
        getVal(img, uv+d.yx)-getVal(img, uv-d.yx)
    )/delta;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = texture(iChannel0, uv).xyz;

    float normalAmount = 250.0;
    vec3 n = vec3(getGrad(iChannel0, uv,1.0/iResolution.y), normalAmount);
    n = normalize(n);

    #ifdef SHOW_NORMALS
    fragColor = vec4(n, 1.0);
    return;
    #endif

    vec3 lightPos = vec3(1,1,2);
    lightPos.x = (1.0 - (iMouse.x / iResolution.x)) * 2.0 - 1.0;
    vec3 light = normalize(lightPos);

    float diff = clamp(dot(n,light),0.5,1.0);
    float spec = clamp(dot(reflect(light,n),vec3(0,0,-1)),0.0,1.0);
    spec = pow(spec,36.0)*2.5;

    #ifdef SHOW_SPECULAR
    fragColor = vec4(vec3(spec), 1.0);
    return;
    #endif

    #ifdef SHOW_DIFFUSE
    fragColor = vec4(vec3(diff), 1.0);
    return;
    #endif

    col = col * vec3(diff) + vec3(spec);

    fragColor = vec4(col,1.0);
}
