renderpass.code:
float hash21(vec2 p) {
    // mod here
    p = mod(p, 6.);
    return fract(43758.5453123 * sin(dot(p, vec2(12.988, 243.233))));
}

float vnoise(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);
    f = f * f * (3. - 2. * f);
    return mix(mix(hash21(i), hash21(i + vec2(1, 0)), f.x),
        mix(hash21(i + vec2(0, 1)), hash21(i + vec2(1, 1)), f.x), f.y);
}


void mainImage(out vec4 O, in vec2 I) {

    vec2 R = iResolution.xy;
    vec2 uv = (2. * I - R.xy) / R.y;

    uv *= 50.+iTime;
    uv = vec2(atan(uv.x, uv.y), length(uv));
    //uv *= 5.+iTime*2.0;

    uv =uv+ sin( 5.+iTime*2.0);

    // scale here
    float v = vnoise(uv * 6. / 6.283);
    vec3 col = vec3(v);


    O = vec4(col, 1.);

}


