const float tau = 6.283185307179586476925286766559;
const vec3 background_color = vec3(1.0);
const vec3 central_disc_color = vec3(1.0, 0.66, 0.33);
const vec3 external_disc_color = vec3(0.0);

float disc(vec2 uv, vec2 center, float radius)
{
	vec2 delta = (uv-center)/radius;
    float sqDistance = dot(delta, delta);
    return smoothstep(1.01, 0.99, (sqDistance));
}

void blob(
    inout vec3 fragColor,
    in vec2 uv,
    in vec2 center,
    in float radius,
    in vec3 color,
    in int e_count,
    in float e_big_radius,
    in float e_small_radius,
	in vec3 e_color)
{
    fragColor = mix(fragColor, color, disc( uv, center, radius));
    for (int i = 0; i < e_count; ++i)
    {
        float angle = tau * float(i) / float(e_count);
        vec2 e_center = center + e_big_radius * vec2( cos(angle), sin(angle) );
        fragColor = mix(fragColor, e_color, disc( uv, e_center, e_small_radius ));
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.yy;

    vec3 col = background_color;

    vec3 e_color = external_disc_color;
    float posRad1 = mix( 0.16, 0.31, clamp(cos(iTime)+0.5, 0., 1.) );
    float posRad2 = mix( 0.31, 0.16, clamp(cos(iTime)+0.5, 0., 1.) );

    float smallRad1 = mix( 0.14, 0.045, clamp(cos(iTime)+0.5, 0., 1.) );
    float smallRad2 = mix( 0.045, 0.14, clamp(cos(iTime)+0.5, 0., 1.) );

    blob(col, uv, vec2(-0.4,0), 0.1, central_disc_color, 6, posRad1, smallRad2, e_color);
    blob(col, uv, vec2( 0.4,0), 0.1, central_disc_color, 6, posRad2, smallRad1, e_color);

    fragColor = vec4(col,1.0);
}
