void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - .5 -iTime*.005;
    vec3 c = cos(vec3(iTime*.006,
                  iTime*.025,
                  iTime*.0095)*3.)*2.+2.;
    for (int i = 0; i < 27; i++) {
        vec3 p = vec3(uv*float(i),float(i));
    	c += vec3( sin(c.y+sin(p.x)),
                   cos(c.z+sin(p.z)),
                   -sin(c.x+sin(p.y)) );
    }
    fragColor = vec4(c*c*.004,1.);
}
