void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float space = 1.15;
    float count = 5.;
    float speed = 20.;

    float col = 5.;
    float r = iResolution.x / ((count + space) * 2.);

    fragCoord.x = mod(fragCoord.x - iTime * speed, r * (2. + space - 1.));
    fragCoord.y = mod(fragCoord.y + iTime * speed, r * (2. + space - 1.));

    fragColor = vec4(1. - pow(distance(vec2(r, r), fragCoord) / r, 2.)) * vec4(1., 1., 0., 1.);
}
