#define PI 3.141529

vec3 lineTexture(vec2 uv)
{	
    vec2 lines = uv.yx * uv.yy + uv.xx;
    for(int i=1; i<int(4.); i++){
        lines.x+=.15*sin(lines.y+iTime*.75);
        lines.y+=.35*cos(lines.x+iTime*.2);
    }
    float outLines = cos(lines.y+lines.x+2.)*.5+.5 * PI;
    vec3 col = vec3(outLines);
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = gl_FragCoord.xy / iResolution.xy;
    uv += iTime*.005;
    float radius = iResolution.y / 11.;
    vec3 colChan0 = texture(iChannel0, vec2(lineTexture(uv))).rgb;
	vec3 colChan1 = texture(iChannel1, vec2(lineTexture(uv))+iTime*.12).rgb;
    vec3 col = vec3(0.); 
	col += colChan0;
    col *= mix(col, colChan1, colChan0.r * 2.);
    fragColor = vec4(col,1.0);
}
