float circle(in vec2 p, float r)
{
    return abs(length(p) - r);
}

float line(in vec2 p, in vec2 a, in vec2 b, float r)
{
    vec2 ba = b - a;
    vec2 pa = p - a;

    float t = clamp(dot(pa, ba) / dot(ba, ba), 0., 1.);

    return length(pa - ba * t) - r;
}

float horseshoe(in vec2 p, float t, float r)
{
    float b = t * 3.14 * 2.;
    float a = atan(p.y, p.x) / 3.14 * .5 + .5; 
    return (a < t) ? 
        				min(length(p + vec2(r, 0.)), 
                            length(p * mat2(cos(b), sin(b), -sin(b), cos(b)) + vec2(r, .0)))
        				:
    					abs(length(p) - r);
}

float h(in vec2 p, float s)
{
    p *= 1. / s;

    float l1 = line(p, vec2(-.5, 1.), vec2(-.5, -1.), .0);
    float hs = horseshoe(p + vec2(.0, .5), .5, .5);
    float l2 = line(p, vec2(.5, -.5), vec2(.5, -1.), .0);

    return min(l2, min(l1, hs));
}


float o(in vec2 p, float s)
{
    p *= 1. / s;

    return circle(p + vec2(.0, .5), .5);
}

float l(in vec2 p, float s)
{
    p *= 1. / s;

    return line(p, vec2(.0, 1.), vec2(.0, -1.), .0);
}

float a(in vec2 p, float s)
{
    p *= 1. / s;

    float c = circle(p + vec2(.0, .5), .5);
    float l = line(p, vec2(.5, .0), vec2(.5, -1.), 0.);

    return min(c, l);
}

float m(in vec2 p, float s)
{
    p *= 1. / s;

    float l1 = line(p, vec2(-1., .0), vec2(-1.), .0);
    float hs = horseshoe(vec2(abs(p.x)-.5, p.y + .5), .5, .5);
    float l2 = line(p, vec2(.0, -.5), vec2(.0, -1.), .0);
    float l3 = line(p, vec2(1., -.5), vec2(1., -1.), .0);

    return min(l3, min(l2, min(l1, hs)));
}

float u(in vec2 p, float s)
{
    p *= 1. / s;

    float l1 = line(p, vec2(-.5, .0), vec2(-.5), .0);
    float hs = horseshoe(vec2(p.x, -p.y - .5), .5, .5);
    float l2 = line(p, vec2(.5, .0), vec2(.5, -1.), .0);

    return min(l2, min(l1, hs));
}

float n(in vec2 p, float s)
{
    p *= 1. / s;

    float l1 = line(p, vec2(-.5, .0), vec2(-.5, -1.), .0);
    float hs = horseshoe(p + vec2(.0, .5), .5, .5);
    float l2 = line(p, vec2(.5, -.5), vec2(.5, -1.), .0);

    return min(l2, min(l1, hs));
}

float d(in vec2 p, float s)
{
    p *= 1. / s;

    float c = circle(p + vec2(.0, .5), .5);
    float l = line(p, vec2(.5, 1.), vec2(.5, -1.), 0.);

    return min(c, l);
}

#define R vec3(1., .0, .0)
#define Y vec3(1., 1., .0)
#define G vec3(.0, 1., .0)
#define C vec3(.0, 1., 1.)
#define B vec3(.0, .0, 1.)
#define M vec3(1., .0, 1.)
#define T iTime 
#define Pi 3.14

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;

    vec3 col = vec3(0.);

    float s = .1;
    float ch = h(uv + vec2(.66, .0), s);
    float co = o(uv + vec2(.52, .0), s);
    float cl = l(uv + vec2(.43, .0), s);
    float ca = a(uv + vec2(.34, .0), s);

    float cm = m(uv - vec2(.0, .0), s);
    float cu = u(uv - vec2(.19, .0), s);
    float cn = n(uv - vec2(.33, .0), s);
    float cd = d(uv - vec2(.47, .0), s);
    float co2 = o(uv - vec2(.61, .0), s);

    float c = min(ch, min(co, min(cl, min(ca, min(cm, min(cu, min(cn, min(cd, co2))))))));
    float sc = smoothstep(.15, .2, c);
    c = smoothstep(.05, .1, abs(fract(c-iTime)-.5));

    float a = mod(T*.25, 6.);

    vec3 CL = (a >= .0 && a < 1.) ? 	
        		mix(R, Y, a) : 
    			(a >= 1. && a < 2.) ?
                mix(Y, G, a - 1.) :
    			(a >= 2. && a < 3.) ?
                mix(G, C, a - 2.) : 
    			(a >= 3. && a < 4.) ?
                mix(C, B, a - 3.) :
    			(a >= 4. && a < 5.) ?
                mix(B, M, a - 4.) :
    			mix(M, R, a- 5.);

    col += mix(CL, vec3(1.), min(c, sc)) + .5;

    fragColor = vec4(sqrt(col), 1.);
}
