void mainImage( out vec4 fragColor, in vec2 fragCoord )

{
    // Normalized pixel coordinates (from 0 to 1)


    vec2 uv = fragCoord/iResolution.xy;
    if(mod(fragCoord.y,2.) > 1.0) {
    uv.x+=sin(uv.y*10.0+iTime)*0.0700;
    } else { 
    uv.x+=sin(uv.y*10.0+iTime)*-0.0700;
    }
    uv.y+=sin(uv.x*10.0+iTime)*0.0700;
    // Output to screen
    vec4 uv2 = texture(iChannel1, uv);

    fragColor = uv2;
}
