float random (in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))
                * 43758.5453123);
}

float noise(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);
    vec2 u = f*f*(3.0-2.0*f);
    return mix( mix( random( i + vec2(0.0,0.0) ),
                     random( i + vec2(1.0,0.0) ), u.x),
                mix( random( i + vec2(0.0,1.0) ),
                     random( i + vec2(1.0,1.0) ), u.x), u.y);
}

mat2 rotate2d(float angle){
    return mat2(cos(angle),-sin(angle),
                sin(angle),cos(angle));
}

const int TIMES = 10;
const float ZOOM = 20.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 st = uv*rotate2d(5.);

for(int i = 0; i < TIMES; i++) {
            float dist = distance(st.x, st.y);

            st.x = st.x + sin(st.y + sin(dist*0.5)) + noise(uv)+iTime;
            st.y = st.y - cos(st.x + cos(dist*0.5)) + noise(uv)+iTime;
        }

    fragColor = vec4(noise(sin(st*0.5+iTime)),0.,noise(sin(st/0.2+iTime)), 1.0);
}
