void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).rgb;



    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;

    #ifdef LOCATE_SCAN
    	vec2 p = vec2(uv.x, fract(-iTime * SCAN_SPEED));
    #else
    	vec2 p = vec2(uv.x, abs(fract(iTime * SCAN_SPEED) * 2.0 - 1.0));
    #endif

    vec3 val = texture(iChannel0, p).rgb;

    #ifdef LOCATE_SCAN
        float k = CURVE_SCALE / iResolution.y;
        val *= k;
        val += p.y * (1.0 + k) - k;
    #endif

    vec3 col = 1.0 - step(1.0 / iResolution.y, abs(val.rgb - uv.yyy));

    vec2 bg_pos = uv;


    #ifdef LOCATE_SCAN
    	float mix_weight = 0.99;
    #else
    	float mix_weight = 0.97;
    #endif

    col =  max(col, mix_weight * texture(iChannel1, bg_pos).rgb);



    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    #ifdef LOCATE_SCAN
    float frozen = step(0.1, fract(iTime * SCAN_SPEED));
    #else
    float frozen = 0.0;
    #endif
    vec3 col = mix(texture(iChannel0, uv).rgb, texture(iChannel1, uv).rgb, frozen);



    // Output to screen
    fragColor = vec4(col,1.0);
}
